import React from "react";
import { useNewArrivals } from "../../Context/new-arrival-context";
import { ProductCard } from "../../index";
import Lottie from "react-lottie";
import LoadingLottie from "../../Assets/Lottie/loading-0.json";

// Plant names
const PLANT_NAMES = [
  "Money Plant",
  "Snake Plant",
  "Fiddle Leaf Fig",
];

// Updated plant images
const PLANT_IMAGES = [
  "data:image/jpeg;base64,", // Money Plant
  "data:image/jpeg;base64,", // Snake Plant
  "data:image/jpeg;base64,", // Fiddle Leaf Fig
];

function NewArrivals() {
  const { newArrivalsProductList } = useNewArrivals();

  const loadingObj = {
    loop: true,
    autoplay: true,
    animationData: LoadingLottie,
    rendererSettings: {
      preserveAspectRatio: "xMidYMid slice",
    },
  };

  return (
    <div className="new-arrivals-container">
      {newArrivalsProductList.length === 0 ? (
        <Lottie options={loadingObj} height={380} />
      ) : (
        newArrivalsProductList.slice(0, 3).map((product, index) => (
          <ProductCard
            key={product._id}
            productdetails={{
              ...product,

              // ðŸŒ± FRONTEND OVERRIDE (SAFE)
              bookName: PLANT_NAMES[index] || "Plant",
              author: "Indoor Plant",
              imgSrc: PLANT_IMAGES[index] || PLANT_IMAGES[0],
              imgAlt: PLANT_NAMES[index] || "Plant",
            }}
          />
        ))
      )}
    </div>
  );
}

export { NewArrivals };
