import React, { useEffect } from "react";
import jwt_decode from "jwt-decode";
import { useLocation } from "react-router-dom";
import "./Shop.css";
import {
  Sidebar,
  ProductCard,
  useWishlist,
  useCart,
  useSearchBar,
} from "../../index.js";
import { useProductAvailable } from "../../Context/product-context";
import axios from "axios";

// ðŸŒ± fallback plant images (6 plants)
const PLANT_NAMES = [
  "Money Plant",
  "Snake Plant",
  "Peace Lily",
  "Fiddle Leaf Fig",
  "Aloe Vera",
  "ZZ Plant",
];

const PLANT_IMAGES = [
  "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcR4HSvi6UhTRFzsCMWKsiZ3h-x8IHbxW7dZfg&s",
  "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcToZ5LmC9jZq18jnjnw0Lj7Cmqg2Tig_eHa7w&s",
  "data:image/jpeg;base64,",
 "data:image/jpeg;base64,",
  "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQWpG1yjjnXR3tFiaUoGmGB31oNxgRQaB1XAw&s",
  "data:image/jpeg;base64,"
];

function Shop() {
  const { productsAvailableList, dispatchSortedProductsList } =
    useProductAvailable();
  const { dispatchUserWishlist } = useWishlist();
  const { dispatchUserCart } = useCart();
  const { pathname } = useLocation();
  const { searchBarTerm } = useSearchBar();

  useEffect(() => {
    window.scrollTo(0, 0);
  }, [pathname]);

  // fetch products
  useEffect(() => {
    if (productsAvailableList.length === 0) {
      (async () => {
        try {
          const res = await axios.get(
            "https://bookztron-server.vercel.app/api/home/products"
          );

          dispatchSortedProductsList({
            type: "ADD_ITEMS_TO_PRODUCTS_AVAILABLE_LIST",
            payload: res.data.productsList,
          });
        } catch (err) {
          console.log(err);
        }
      })();
    }
  }, []);

  // fetch wishlist & cart
  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) return;

    const user = jwt_decode(token);
    if (!user) {
      localStorage.removeItem("token");
      return;
    }

    (async () => {
      try {
        const res = await axios.get(
          "https://bookztron-server.vercel.app/api/user",
          {
            headers: { "x-access-token": token },
          }
        );

        if (res.data.status === "ok") {
          dispatchUserWishlist({
            type: "UPDATE_USER_WISHLIST",
            payload: res.data.user.wishlist,
          });
          dispatchUserCart({
            type: "UPDATE_USER_CART",
            payload: res.data.user.cart,
          });
        }
      } catch (err) {
        console.log(err);
      }
    })();
  }, []);

  // âœ… Override backend products with custom plant data
  const filteredProducts = productsAvailableList
    .slice(0, 6) // ðŸ‘‰ show 6 plants
    .map((product, index) => ({
      ...product,
      plantName: PLANT_NAMES[index % PLANT_NAMES.length],
      category: "Indoor Plant",
      imgSrc: PLANT_IMAGES[index % PLANT_IMAGES.length],
      tag: "Plant",
      badge: "New Arrival",
    }))
    .filter((item) =>
      item.plantName.toLowerCase().includes(searchBarTerm.toLowerCase())
    );

  return (
    <div className="shop-container">
      <Sidebar />

      <div className="products-container">
        <h2>Showing {filteredProducts.length} Plants</h2>

        <div className="products-card-grid">
          {filteredProducts.map((item) => (
            <ProductCard key={item._id} productdetails={item} />
          ))}
        </div>
      </div>
    </div>
  );
}

export { Shop };
